import '../App.css';

import React, {useEffect, useRef, useState} from "react";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Button, Form, Spinner} from "react-bootstrap";
import Canvas from "./Canvas";


function TriangulatePoligonVisualization() {
    const [n, setN] = useState(null);
    const [polygons, setPolygons] = useState(null)
    const [isLoading, setIsLoading] = useState(false);

    const canvasRef = useRef(null);
    const contextRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        contextRef.current = context;
    }, []);

    function handleInputChange(event) {
        setN(event.target.value);
    }

    function crtajPoligon(tacke, n){
        var randomColor = Math.floor(Math.random()*16777215).toString(16);
        contextRef.current.beginPath();
        contextRef.current.moveTo(tacke[0][0], tacke[0][1]);
  
        for (let i = 1; i < n; i++) {
            contextRef.current.lineTo(tacke[i][0], tacke[i][1]);
        }
        contextRef.current.closePath();
        contextRef.current.strokeStyle = "#" + randomColor
        contextRef.current.stroke();
    }

    function clearCanvas() {
        contextRef.current.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
    }
    function decompose(polygons){
        for (let index = 0; index < polygons.length; index++) {
            crtajPoligon(polygons[index], polygons[index].length)
            
        }
    }
    function getData(){
        setIsLoading(true); // set isLoading to true before making the API call

        const obj = { n: parseInt(n) };

        axios.post('/triangulate-polygon/', {...obj})
            .then((res) => {
                console.log('Done!');

                setN('');
                setIsLoading(false);
                clearCanvas();

                // draw
                const tacke = res.data.vrhovi;
                const tackeP = res.data.vrhoviP
                setPolygons(tackeP)
                crtajPoligon(tacke, tacke.length)
                
            }).catch((err) => {
            console.log(err);
        })
    }

    return (
        <div className="App">
            <div className="App-header">

                <h4 className="heading">Triagulation of Simple Polygon: Sweep Line Approach with <i>O(n log n)</i> Time Complexity</h4>

                <Form>
                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Control
                            type="number"
                            placeholder="Enter n"
                            onChange={handleInputChange}
                            value={n}
                            disabled={isLoading}
                        />
                        <Form.Text className="text-muted">
                            Algorithm will randomly generate <i>n</i> points and create simple polygon.
                        </Form.Text>
                    </Form.Group>
                    {isLoading ?
                        <Spinner
                            animation="border"
                            variant="light"
                        />
                        :
                        <div>
                            <Button
                                onClick={(e)=>{
                                    e.preventDefault();
                                    getData();
                                }}
                                type={"submit"}
                                disabled={!n}>
                                Create polygon
                            </Button>
                            <Button
                            onClick={(e)=>{
                                e.preventDefault();
                                decompose(polygons);
                            }}
                            type={"submit"}
                            disabled={!polygons}>
                            Decompose polygon
                            </Button>
                        </div>
                    }
                </Form>

                <Canvas
                    width={1000}
                    height={550}
                    canvasRef={canvasRef}
                />
                <p
                    className="secondary-heading"
                    onClick={clearCanvas}>
                    Clear board
                </p>

            </div>
        </div>
    );
}

export default TriangulatePoligonVisualization;
